  def valid_attributes
    {}
  end

  describe "GET index" do
    it "assigns all cannons as @cannons" do
      cannon = Cannon.create! valid_attributes
      get :index
      assigns(:cannons).should eq([cannon])
    end
  end

  describe "GET show" do
    it "assigns the requested cannon as @cannon" do
      cannon = Cannon.create! valid_attributes
      get :show, :id => cannon.id.to_s
