  end

  describe "GET edit" do
    it "assigns the requested cannon as @cannon" do
      cannon = Cannon.create! valid_attributes
      get :edit, :id => cannon.id.to_s
      assigns(:cannon).should eq(cannon)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "creates a new Cannon" do
        expect {
          post :create, :cannon => valid_attributes
        }.to change(Cannon, :count).by(1)
      end

      it "assigns a newly created cannon as @cannon" do
        post :create, :cannon => valid_attributes
        assigns(:cannon).should be_a(Cannon)
