        Cannon.any_instance.stub(:save).and_return(false)
        post :create, :cannon => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested cannon" do
        cannon = Cannon.create! valid_attributes
